import { Component, OnInit } from '@angular/core';
import { DataService } from 'src/app/service/data/data.service';

@Component({
  selector: 'app-informationpage',
  templateUrl: './informationpage.component.html',
  styleUrls: ['./informationpage.component.css']
})
export class InformationpageComponent implements OnInit {
  user: any; // Assurez-vous que le type correspond à votre modèle utilisateur

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    // Exemple: Appelez votre service pour récupérer les informations de l'utilisateur
    this.dataService.getUserInformation().subscribe(
      (user) => {
        this.user = user;
      },
      (error) => {
        console.error('Erreur lors de la récupération des informations utilisateur', error);
      }
    );
  }
}
