@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  user = {
    email: '',
    password: ''
  };

  onSubmit() {
    console.log('Form submitted:', this.user);
    // Ajoutez ici la logique pour gérer la soumission du formulaire (envoi à votre backend, etc.)
  }
}
